h1,
h2,
h3,
h4,
h5,
h6 {
    font-family: 'Function Pro';
    font-weight: 700;
    line-height: 1;
    text-transform: uppercase;
}

h1 {
    font-size: $size-1;
}

h2 {
    font-size: $size-2;
}

h3 {
    font-size: $size-3;
}

h4 {
    font-size: $size-4;
}

h5 {
    font-size: $size-5;
}

h6 {
    font-size: $size-6;
}

ul,
ol {
    list-style-position: outside;
    margin-left: $baseline * 1.5;
}

ul {
    list-style-type: disc;
}

li+li {
    margin-top: $baseline * 0.25;
}

dd+dt {
    margin-top: $baseline * 0.25;
}

a {
    text-decoration: underline;
    text-decoration-skip: ink;
    text-decoration-color: rgba($link, 0.25);
    &:hover {
        text-decoration-color: rgba($link-hover, 0.25);
    }
    &:focus {
        text-decoration-color: rgba($link-focus, 0.25);
    }
    &:active {
        text-decoration-color: rgba($link-active, 0.25);
    }
}

.button {
    background: #fe1135;
    border: 2px solid transparent;
    color: #fff;
    cursor: pointer;
    font-family: 'Function Pro', sans-serif;
    font-size: $size-5;
    font-weight: 700;
    line-height: 1;
    padding: 1rem 2rem;
    text-transform: uppercase;
    &:hover {
        background: transparent;
        border-color: #fe1135;
        color: #fe1135;
    }
    &:focus {
        background: transparent;
        border-color: #fe1135;
        color: #fe1135;
        text-decoration: underline;
        text-decoration-color: rgba(#fe1135, 0.25);
    }
}

@import 'components';
body {
    overflow-x: initial;
}

// For styleguide
:host {
    color: $text;
    font-family: 'Proxima Nova';
    font-size: 16px;
    -moz-osx-font-smoothing: grayscale;
    -webkit-font-smoothing: antialiased;
    text-rendering: optimizeLegibility;
    font-weight: 400;
    line-height: 1.5;
    margin: 0;
    padding: 0;
    .section-markup-wrapper {
        overflow: hidden;
        box-sizing: border-box;
    }
}

.feature-quote {
    display: inline-block;
    text-align: center;
    p {
        font-family: 'Function Pro', sans-serif;
        font-size: $size-2;
        font-style: italic;
        font-weight: 700;
        line-height: 1;
    }
    cite {
        font-family: 'Function Pro', sans-serif;
        font-size: $size-6;
        font-style: normal;
        line-height: 1;
        opacity: 0.75;
    }
    &.large {
        p {
            font-size: $size-2 * 2;
        }
        cite {
            font-size: $size-5 * 2;
        }
    }
}

